// Function to dynamically load HTML files
function loadDiv(url, containerId, preloaderClass) {
    // Display preloader
    const container = document.getElementById(containerId);
    container.innerHTML = `<div class="${preloaderClass}"><p class="mt-1">Crunching data...</p></div>`;

    // Fetch HTML file
    fetch(url)
        .then(response => response.text())
        .then(html => {
            // Insert HTML into container
            container.innerHTML = html;
        })
        .catch(error => console.error("Error loading HTML:", error));
}
