// Load multiple HTML files with preloaders
window.onload = function () {
    // Example: Load HTML files into different containers with preloaders
    loadDiv("../partials/navbar.html", "header", "preloader", removePreloader);
    loadDiv("../partials/landing.html", "landing", "preloader", removePreloader);
    loadDiv("../partials/whyChuck.html", "whyChuck", "preloader", removePreloader);
    loadDiv("../partials/uniswap.html", "uniswap", "preloader", removePreloader);
    loadDiv("../partials/community.html", "community", "preloader", removePreloader);
    loadDiv("../partials/footer.html", "footer", "preloader", removePreloader);
};

// Callback functions to remove preloaders
function removePreloader() {
    document.getElementById("preloader").style.display = "none";
}